@import '../base';
$nav-color: #9C9D9D;

nav {
    display: flex;
    font-family: Calibri-Regular;
    justify-content: space-between;
    align-items: center;
    color: $nav-color;
    height: 80px;
    box-shadow: 0px 3px 20px #0000001A;
    background-color: #fff;
}

.logo {
    margin-left: 50px;
}

.account {
    display: flex;
    align-items: center;

    >div {
        .fa {
            font-size: 30px;
        }
        .fa-circle {
            font-size: 12px;
            margin-left: -4px;
            color: #FFA500 !important;
            z-index: 0;
        }
        display: flex;
        flex-direction: column;
        transition: all 0.5s linear;
        margin-right: 50px;
        text-align: center;

        &:hover {
            color: black;
        }
    }


}

.search-box {
    display: flex;
    position: relative;
    margin-bottom: 0;
    width: 50%;

    .form-control {
        border: 1px solid #9C9D9D;
    }
}

.has-search .form-control {
    font-family: Calibri-Italic;
    border-radius: 18px;
}



.has-search .form-control-feedback {
    position: absolute;
    z-index: 2;
    display: block;
    width: 2.375rem;
    height: 2.375rem;
    line-height: 2.375rem;
    text-align: center;
    pointer-events: none;
    color: #000;
    background: #FFA500;
    border-radius: 50%;
    right: 2px;
    height: 98%;

}

.filters {
    display: flex;
    flex-direction: column;
    width: 100%;
    position: relative;
    margin: 30px 50px;

    p {
        font-family: Calibri-Bold;
        font-size: 24px;
        line-height: 35px;
    }

    &::after {
        content: "";
        width: 100%;
        border-bottom: solid 1px #AEAEAE;
        position: absolute;
        left: 0;
        z-index: 1;
        bottom: 0px;
        margin-bottom: 0;
    }
}

.filtering {
    display: flex;
    width: 100%;
    justify-content: space-between;
    margin-bottom: 10px;
}

.selected-filters {
    display: flex;
    margin: 10px 0;

    span {
        font-size: 16px;
        line-height: 19px;
        margin-right: 5px;
        font-family: Calibri-Regular;

    }
}

///Media Queries/////
@media only screen and (max-width: 600px) {



    .logo {
        margin-left: 20px;
    }

    .account {

        // &:hover {
        //     span {
        //         display: block;
        //         transform: scale(1.2);
        //     }
        // }
        width: 50%;
        justify-content: space-around;
        margin-right: 0;

        >div {
            margin-right: 20px;

        }

    }

    .profile .hide,
    .orders .hide,
    .cart .hide {
        display: none;
    }

    .profile:hover,
    .orders:hover,
    .cart:hover {
        animation: slide-up;

        .hide {
            display: block;
        }
    }

    .search-box {
        width: 30%;
    }

    .has-search .form-control {
        visibility: hidden;
    }

    .filters {
        margin: 20px;
    }

    .filtering {
        display: none;
    }
}

@keyframes slide-up {
    from {
        margin-top: 100%;
        height: 300%;
    }

    to {
        margin-top: 0%;
        height: 100%;
    }
}

.dropdown-content {
    display: none;
    position: absolute;
    background-color: #f1f1f1;
    min-width: 150px;
    box-shadow: 0px 8px 16px 0px rgba(0, 0, 0, 0.2);
    z-index: 1;
}
.cart-container {
    display: flex;
}
.dropdown-content a {
    color: black;
    padding: 12px 16px;
    text-decoration: none;
    display: block;
}



.dropdown-content a:hover {
    background-color: #ddd;
}

.selection:hover .dropdown-content {
    display: block;
    z-index: 2;
}

.selection:hover .dropbtn {
    background-color: #3e8e41;
}

