.product-list-container {
    margin: 30px 50px;
    width: 100%;
}

.product-list {
    width: 100%;
}

.product-list-box.tablet-only {
    display: none;
}

.product-list-box {
    background: white;
    display: grid;
    grid-template-columns: repeat(8, 1fr);
    grid-gap: 20px;
    align-items: center;
    padding: 20px;
    background: white;
    font-family: Calibri-Regular;
    margin-bottom: 20px;

    .product-list-title {
        font-family: Calibri-Bold;
    }

    .product-list-image {
        text-align: center;

        img {
            max-width: 60px;
        }
    }
}

.product-list-stock>p>span {
    margin-left: 5px;
}

.product-list-title {
    text-decoration: underline;
}

.fa-exclamation-circle {
    color: #FFA500;
}

.fa-times-circle,
.warning {
    color: #8E1216;
}

.product-list-stock {

    .warning,
    .stock-alert {
        font-family: Calibri-Bold;
    }

}

.save-mobile {
    display: none;
}

///------RESPONSIVE BEHAVIOR-------////


/////--TABLET---///
@media only screen and (max-width: 888px) {

    .mobile-bold {
        font-size: 18px;
    }
    .save-mobile {
        display: block;
        color: #8E1216;
    }

    .product-list {
        display: grid;
        grid-template-columns: max-content max-content;
        gap: 20px;
    }

    .product-list-container {
        margin: 20px;
    }

    .product-list-box.tablet-only {
        display: grid;
    }

    .product-list-box {
        margin-bottom: 0;
        font-family: Calibri-Regular;
        font-style: normal;
        gap: 10px;
        grid-template-columns: minmax(150px, 150px) !important;

        .product-list-image {
            grid-row: 2;
            width: max-content;
            grid-column-start: 1;

            img {
                width: 60px;
            }
        }

        .product-list-title {
            width: min-content;
        }

        .product-list-stock {
            grid-column-start: 1;
            grid-row: 2;
            width: min-content;
            margin-left: 70px;

            .stock-alert {
                width: max-content;
            }

            .warning {
                width: max-content;
            }
        }

        .product-list-qty {
            grid-row: 3;
        }

        .product-list-cart {
            grid-column-start: 2;
            grid-row: 3;
        }

        .product-list-total {
            grid-row: 2;
            grid-column-start: 2;
            margin-left: 70px;
        }

        .product-list-price {
            grid-row: 2;
            grid-column-start: 2;
            width: min-content;
        }
    }
}



/////--------------Mobile----------------/////
@media only screen and (max-width: 500px) {

    .product-list-box.tablet-only {
        display: none;
    }

    .product-list {
        display: grid;
        grid-template-columns: 1fr;
    }

    .product-list-box {
        grid-template-columns: minmax(100px, auto) !important;
        padding: 10px;

        .product-list-title {
            width: initial;
        }
        .product-list-price {
            margin-left: 25px;
        }

        .product-list-name {
            margin-left: 25px;
        }
    }

}

