    background-color: #FFA500;
    border: none;
    border-radius: 30px;
    padding: 10px 20px;
    display: flex;
    align-items: center;
    cursor: pointer;
    img {
        width: 30px;
        margin-right: 10px;
    }
}

@media only screen and (min-width: 1025px) {
    .mobshow {
        display: none;
    }
}
@media only screen and (max-width: 1024px) {
    .deskshow {
        display: none;
    }
    .best-seller-container {
        padding: 40px 15px 15px;
    }
    .bs-image {
        width: 35%;
        .image-tag {
            width: 30px;
        }
    }
    .bs-content {
        width: 65%;
        .bs-title {
            font-size: 18px;
        }
    }
    .bs-content-inner {
        flex-wrap: wrap;
        .ps-title {
            margin-right: 0;
            display: block;
            width: 100%;
        }
        .product-tag {
            width: 28%;
        }
        .product-model {
            width: 64%;
            > *:not(:first-child) {
                margin-top: 0;    
            }
        }
        .product-stock {
            width: 60%;
            margin-left: 0;
            display: flex;
            margin-top: 10px;
            > * {
                width: 50%;
            }
            .stock-status {
                text-align: center;
            }
            .product-price {
                margin-top: 0;
                padding-left: 4%;
            }
        }
        .product-total {
            width: 36%;
            margin-top: 10px;
        }
        .pt-detail {
            margin-top: 0 !important;
        }
    }
    .add-cart-btn {
        padding: 8px 15px;
        img {
            width: 22px;
            margin-right: 5px;
        }
    }
}
@media only screen and (max-width: 767px) {
    .best-sellers {margin: 0;padding: 0 20px;display: flex;flex-wrap: wrap;}
    .best-seller-container {width: 100%;margin-top: 19px;}
    .best-seller-container:not(:first-child) {margin-top: 20px;}
    .best-seller-container:before {padding: 7px 10px;top: -19px}
}

